import React, { useState, useEffect } from 'react';
import '../static/styles_';

// --- Helper Components (for organization) ---

// The Header component (based on the image)
const Header = () => (
  <header className="header">
    <div className="logo-container">
      {/* Assuming the logo image is in src/assets */}
      {/* <img src="/path/to/joddb-logo.png" alt="JODDB Logo" className="logo" /> */}
      <span className="logo-placeholder">[JODDB Logo]</span>
    </div>
    <div className="user-container">
      <div className="user-icon">[User Icon]</div>
      <span className="username">Username</span>
      <span className="language-toggle">عربي</span>
      <div className="menu-icon">[Menu]</div>
    </div>
  </header>
);

// State 1: Initial view with 3 buttons
const InitialView = ({ onStartNewTask }) => (
  <div className="initial-view">
    <button className="btn btn-primary">Notifications</button>
    <button className="btn btn-primary">Submitted Tasks</button>
    <button className="btn btn-primary" onClick={onStartNewTask}>
      Start a New Task
    </button>
  </div>
);

// The 3-column layout for 'pending', 'active', and 'paused' states
const TaskView = ({ taskState, handlers }) => {
  // Simple timer logic
  const [time, setTime] = useState(660); // 11:00 in seconds

  useEffect(() => {
    let interval = null;
    if (taskState === 'active') {
      interval = setInterval(() => {
        setTime(prevTime => prevTime + 1);
      }, 1000);
    } else {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [taskState]);

  // Format time as MM:SS
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60).toString().padStart(2, '0');
    const secs = (seconds % 60).toString().padStart(2, '0');
    return `${mins}:${secs}`;
  };

  return (
    <div className="task-view-container">
      {/* === Left Column === */}
      <div className="task-sidebar">
        <div className="task-icon-placeholder">[Task Icon]</div>
        <button className="btn btn-primary" onClick={handlers.handleStartNewTask}>
          START A NEW TASK
        </button>
      </div>

      {/* === Middle Column (Task Details) === */}
      {taskState === 'pending' && (
        <div className="task-details-section">
          <h2 className="section-title">TASK DETAILS</h2>
          <form className="task-form">
            <div className="form-group">
              <input type="text" placeholder="PERFORMED TASK" />
            </div>
            <div className="form-group">
              <input type="text" placeholder="SERIAL NUMBER" />
            </div>
            <div className="form-group">
              <input type="text" placeholder="SELECT NUMBERS OF DEVICES" />
            </div>
            <div className="button-group">
              <button type="button" className="btn btn-secondary" onClick={handlers.handleChangeTask}>
                CHANGE
              </button>
              <button type="button" className="btn btn-primary" onClick={handlers.handleStartTask}>
                START
              </button>
            </div>
          </form>
        </div>
      )}

      {/* === Right Column (Task Timer) === */}
      {(taskState === 'active' || taskState === 'paused') && (
        <div className="task-timer-section">
          <h2 className="section-title">TASK HAS STARTED</h2>
          <div className="timer-display">
            {formatTime(time)}
          </div>
          <div className="button-group">
            {taskState === 'active' ? (
              <button type="button" className="btn btn-secondary" onClick={handlers.handlePauseTask}>
                PAUSE
              </button>
            ) : (
              <button type="button" className="btn btn-secondary" onClick={handlers.handleResumeTask}>
                RESUME
              </button>
            )}
            <button type="button" className="btn btn-primary" onClick={handlers.handleFinishTask}>
              FINISH
            </button>
          </div>
        </div>
      )}
    </div>
  );
};


// --- Main Page Component ---

const StartingTask = () => {
  // States: 'idle', 'pending', 'active', 'paused'
  const [taskState, setTaskState] = useState('idle');

  const handlers = {
    handleStartNewTask: () => setTaskState('pending'),
    handleChangeTask: () => console.log('Change Task Clicked'),
    handleStartTask: () => setTaskState('active'),
    handlePauseTask: () => setTaskState('paused'),
    handleResumeTask: () => setTaskState('active'),
    handleFinishTask: () => {
      console.log('Task Finished. Transition to Page 3.');
      setTaskState('idle'); // Go back to initial view
    },
  };

  return (
    <div className="page-container">
      <Header />
      <main className="main-content">
        {taskState === 'idle' ? (
          <InitialView onStartNewTask={handlers.handleStartNewTask} />
        ) : (
          <TaskView taskState={taskState} handlers={handlers} />
        )}
      </main>
    </div>
  );
};

export default StartingTask;
